import { Avatar, Container, Typography } from "@mui/material";
import React from "react";
import image from "../../../../assets/images/pattern_dot.svg";
import { useTheme } from "@mui/material/styles";
import useMediaQuery from "@mui/material/useMediaQuery";
import { Grid } from "@mui/material";
import { Box } from "@mui/system";
import { Email } from "@mui/icons-material";

const ContactDetails = () => {
  let hideImage, align, width, wdt;
  const theme = useTheme();
  const xs = useMediaQuery(theme.breakpoints.only("xs"));

  if (xs) {
    hideImage = true;
    align = "center";
    width = "96%";
    wdt = "25%";
  } else {
    hideImage = false;
    align = "left";
    width = "70%";
    wdt = "40%";
  }

  return (
    <div>
      <Container
        sx={{
          paddingTop: 4,
          paddingBottom: 4,
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <Grid container>
          <Grid item xs={12} sm={5} md={6}>
            <Box
              display={"flex"}
              flexDirection="row"
              justifyContent={"start"}
              alignItems="start"
            >
              <div
                style={{
                  display: "flex",
                  flexDirection: "row",
                  justifyContent: "left",
                  alignItems: "left",
                }}
              >
                <img
                  src={image}
                  alt="contact-pattern"
                  width={wdt}
                  //   style={{ marginBottom: -16, marginLeft: 48 }}
                />
              </div>
              <div>
                <Typography fontWeight={"500"} fontSize={32} color="#012F74">
                  Contact Details
                </Typography>
                <div
                  style={{
                    display: "flex",
                    flexDirection: "row",
                    justifyContent: "space-between",
                    alignItems: "center",
                  }}
                >
                  <Avatar
                    style={{ width: 24 }}
                    variant="circular"
                    sx={{
                      backgroundColor: "#012F74",
                    }}
                  >
                    <Email fontSize="16" />
                  </Avatar>
                  <Typography>info@rsphcmb.org.ng</Typography>
                </div>
              </div>
            </Box>
          </Grid>
          <Grid item xs={12} sm={7} md={6}></Grid>
        </Grid>
      </Container>
    </div>
  );
};

export default ContactDetails;
