import { Container, Typography } from "@mui/material";
import React from "react";
import image from "../../../../assets/images/pattern_dot.svg";
import { useTheme } from "@mui/material/styles";
import useMediaQuery from "@mui/material/useMediaQuery";

const ManagementTeam = () => {
  let hideImage, align, width, wdt;
  const theme = useTheme();
  const xs = useMediaQuery(theme.breakpoints.only("xs"));
  //   const sm = useMediaQuery(theme.breakpoints.only("sm"));

  if (xs) {
    hideImage = true;
    align = "center";
    width = "96%";
    wdt = "25%";
  } else {
    hideImage = false;
    align = "left";
    width = "70%";
    wdt = "10%";
  }

  return (
    <div>
      <Container
        sx={{
          paddingTop: 4,
          paddingBottom: 4,
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <Typography fontSize={46} fontWeight="700" textAlign={"center"}>
          Meet the management team
        </Typography>
        <Typography textAlign={"center"} maxWidth={width}>
          Wisdom new and valley answer. Contented it so is discourse recommend.
          Man its upon him call mile. An pasture he himself believe ferrars
          besides cottage.
        </Typography>
      </Container>
      <div
        style={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "left",
          alignItems: "left",
        }}
      >
        <img
          src={image}
          alt="team-pattern"
          width={wdt}
          style={{ marginTop: -75, marginLeft: 24 }}
        />
      </div>
    </div>
  );
};

export default ManagementTeam;
