import React from "react";
import Carousel from "react-multi-carousel";
import { Button, Typography, Grid } from "@mui/material";

import { useTheme } from "@mui/material/styles";
import useMediaQuery from "@mui/material/useMediaQuery";

import { withRouter, useHistory } from "react-router-dom";
import banner1 from "../../../../assets/images/banner1.png";
import banner2 from "../../../../assets/images/banner2.png";

function handleImageError(event) {
  if (
    this.props.defaultImage &&
    event.target.src.indexOf(this.props.defaultImage) === -1
  ) {
    event.target.src = this.props.defaultImage;
  }
}

function renda(item) {
  const onImageError = handleImageError;
  return (
    <div>
      <p>Hello world</p>
    </div>
  );
}

var responsive = {
  superLargeDesktop: {
    breakpoint: { max: 4000, min: 3000 },
    items: 1,
  },
  desktop: {
    breakpoint: { max: 3000, min: 1024 },
    items: 1,
  },
  tablet: {
    breakpoint: { max: 1024, min: 768 },
    items: 1,
  },
  mobile: {
    breakpoint: { max: 768, min: 0 },
    items: 1,
  },
};

const itemList = [
  { name: "Hello", image: banner1 },
  { name: "Hello", image: banner2 },
];

function Header(props) {
  // var items = [
  //   {
  //     bigText: "Our Scholarship Programs",
  //     description: "We have come to agree that educating young people is part of an industrial company's social responsibility",
  //     bgImage: img1,
  //     btnText: "Enroll Now",
  //     btnLink: "/"
  //   },
  //   {
  //     bigText: "Random Name #2",
  //     description: "Probably the most random thing you have ever seen!",
  //     bgImage: img2,
  //     btnText: "Register",
  //     btnLink: "/"
  //   }
  // ];

  //const { history } = props;
  //   const { data } = useSWR('/setting');

  //   React.useEffect(() => {
  //     if (data) {
  //     }
  //   }, []);

  //   if (!data) return <div>
  //     <Skeleton variant="rect" style={{ height: 526, width: '100%' }} />
  //   </div>

  return (
    <div style={{ backgroundColor: "white" }}>
      <Carousel
        arrows
        showDots
        autoPlay
        infinite={false}
        swipeable
        draggable
        keyBoardControl
        slidesToSlide={1}
        autoPlaySpeed={10000}
        focusOnSelect={false}
        minimumTouchDrag={80}
        responsive={responsive}
        additionalTransfrom={0}
        renderDotsOutside={false}
        renderButtonGroupOutside={false}
      >
        {(itemList ?? [])?.map((item, i) => (
          <Item key={i} item={item} />
        ))}
      </Carousel>
    </div>
  );
}

function Item(props) {
  //   const dispatch = useDispatch();
  const history = useHistory();

  var headingSize, descSize, deviceType;
  const theme = useTheme();
  const xsmallScreen = useMediaQuery(theme.breakpoints.only("xs"));
  const smallScreen = useMediaQuery(theme.breakpoints.only("sm"));
  const mediumScreen = useMediaQuery(theme.breakpoints.up("md"));

  if (xsmallScreen) {
    headingSize = "h5";
    descSize = 15;
    deviceType = "small";
  } else if (smallScreen) {
    headingSize = "h4";
    descSize = 17;
    deviceType = "small";
  } else if (mediumScreen) {
    deviceType = "large";
    headingSize = "h3";
    descSize = 19;
  }

  return (
    <div
      style={
        deviceType === "small"
          ? {
              backgroundImage: "url(" + props.item.image + ")",
              backgroundRepeat: "no-repeat",
              backgroundSize: "cover",
              backgroundPosition: "center",
              paddingTop: 120,
              paddingBottom: 36,
              width: "100%",
            }
          : {
              backgroundImage: "url(" + props.item.image + ")",
              backgroundRepeat: "no-repeat",
              backgroundSize: "cover",
              paddingTop: 128,
              paddingBottom: 50,
              width: "100%",
            }
      }
    ></div>
  );
}

export default withRouter(Header);
