import React from "react";
import imageSigma from "../../../assets/images/coro_sketch.svg";
import image1 from "../../../assets/images/coro1.svg";
import image2 from "../../../assets/images/coro2.svg";
import image3 from "../../../assets/images/coro3.svg";
import imageArc from "../../../assets/images/decorator_home.svg";
import { Container, Typography } from "@mui/material";
import { Box } from "@mui/system";

const Covid19Section = () => {
  return (
    <div
      style={{
        position: "relative",
      }}
    >
      <div
        style={{
          width: "100%",
          display: "flex",
          flexDirection: "row",
          justifyContent: "end",
        }}
      >
        <img src={imageSigma} alt="" width="20%" style={{ marginTop: -24 }} />
      </div>
      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          padding: 30,
          zIndex: 100,
        }}
      >
        <div
          style={{
            width: "100%",
          }}
        >
          <img src={imageArc} alt="" width={"7%"} style={{ marginLeft: -36 }} />
          <Container
            sx={{
              marginTop: -21,
            }}
          >
            <Box
              display="flex"
              flexDirection="column"
              justifyContent="center"
              alignItems={"center"}
            >
              <Typography
                fontSize={56}
                fontWeight="700"
                textAlign="center"
                gutterBottom
              >
                Covid-19 Updates
              </Typography>
              <Typography
                fontSize={16}
                fontWeight="400"
                textAlign="center"
                style={{
                  width: "60%",
                }}
              >
                Since the outbreak of the deadly virus COVID-19, the Rivers
                State Primary Health Care Management Board has taken critical
                steps to ensure that its staff and patients are safe.
              </Typography>
            </Box>
          </Container>
        </div>
      </div>
    </div>
  );
};

const CasesItem = (props) => {
  let { bgColor, title, cases, icon } = props;

  return (
    <div>
      <div
        style={{
          width: 64,
          height: 64,
          borderRadius: 32,
          borderColor: bgColor,
          borderWidth: 1,
          paddin: 8,
        }}
      >
        <img src={icon} alt="" />
      </div>
      <Box
        padding={4}
        bgcolor={bgColor}
        display="flex"
        height={200}
        borderRadius={8}
        flexDirection={"column"}
        justifyContent="center"
        alignItems="center"
      >
        <Typography fontWeight="500" fontSize={16} gutterBottom={true}>
          {title}
        </Typography>
        <Typography fontWeight="500" fontSize={16} gutterBottom={true}>
          {cases}
        </Typography>
      </Box>
    </div>
  );
};

export default Covid19Section;
